package Aula07_Loop;

public class Loop01While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int count = 0;
		
		while (count <= 10) {
			
			System.out.println("Mais " + count);
			
			// count = count + 1;
			
			// count += 1;
			
			count ++;
		}

	}

}
