package Aula07_Loop;

public class Loop04Encadiados {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i = 0; i <= 10; i++) {
			
			for (int j = 0; j <= 10; j++) {
				
				System.out.println("Mais " + "i: " + i + " e Mais " + " j: " + j);
				
			}
			
		}

	}

}
