package Aula07_Loop;

public class Loop05EncadiadosTabuada {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i <= 10; i++) {
			
			for (int j = 0; j <= 10; j++) {
				
				System.out.println("Tabuada do " + i + " x " + j + " = " + i * j);
				
			}
			
			System.out.println();
			
			System.out.println("Esta é a tabuada do " + i);
			
			System.out.println("*************************");
			
		}

	}

}
